import { shuffle, createAddList, createSubList, getChoices } from "../../app/common/math";
import { Problem1 } from "../math1/math1Slice";
import { Problem2 } from "./math2Slice";

export function createProblemList() {
  const addList = createAddList();
  const subList = createSubList();

  const list: Problem2[] = [];
  addList.forEach((p1) => {
    list.push(...addList.filter((p2) => p2.num1 === p1.answer && p1.answer + p2.num2 <= 10).map((p2) => combineProblems(p1, p2, p1.answer + p2.num2)));
    list.push(...addList.filter((p2) => p2.num1 === p1.answer && p1.answer + p2.num2 <= 10).map((p2) => combineProblems(p1, p2, p1.answer + p2.num2)))

  })
  return shuffle([...createAddList(), ...createSubList()]);
}

function combineProblems(p1: Problem1, p2: Problem1, answer: number): Problem2 {
  return {
    num1: p1.num1,
    num2: p1.num2,
    num3: p2.num2,
    ops1: p1.ops,
    ops2: p2.ops,
    choices: getChoices(answer),
    answer,
  };
}
