export function createAddList(ops: '+' | '-') {
  const list = [];
  for (let i = 0; i <= 10; i++) {
    for (let j = 0; j <= 10-i; j++) {
      const answer = ops === '+' ? i+j : i-j;
      if (answer < 0) {
        break;
      }

      list.push({
        num1: i,
        num2: j,
        ops,
        answer,
        choices: getChoices(answer),
      })
    }
  }
}

export function getChoices(answer: number) {
  const min = answer > 7 ? answer-7 : 0;
  const max = answer > 3 ? 3 : answer;
  const index = randomizer(min, max);

  const choices = [];
  for (let i = 0; i < 3; i++) {
    choices.push(answer+i-index);
  }
  return choices;
}

function randomizer(min: number, max: number) { // min and max included 
  return Math.floor(Math.random() * (max - min + 1) + min);
}

function shuffle(array: string[]) { 
  for (let i = array.length - 1; i > 0; i--) { 
    const j = Math.floor(Math.random() * (i + 1)); 
    [array[i], array[j]] = [array[j], array[i]]; 
  } 
  return array; 
};
