import { get1DigitCommonProblem, randomizer, shuffle } from "./math1Digit";

export function create2DigitsSubList() {
    const list = [];

    for (let i = 1; i <= 100; i++) {
      for (let j = 1; j < i; j++) {
        const num3 = i - j;

        list.push({
          num1: i,
          num2: j,
          num3,
          ops: "-",
          ...get1DigitCommonProblem([i, j, num3]),
        });
      }
    }

    return list;
}

export function create2DigitsAddList() {
    const list = [];

    for (let i = 1; i <= 100; i++) {
      for (let j = 1; j <= 100 - i; j++) {
        const num3 = i + j;

        list.push({
          ...get1DigitCommonProblem([i, j, num3]),
          num1: i,
          num2: j,
          num3,
          ops: "+",

        });
      }
    }

    return list;
}

export function get2DigitsChoices(answer: number): number[] {
    const min = answer > 97 ? answer - 97 : 0;
    const max = answer > 3 ? 3 : answer;
    const index = randomizer(min, max);

    const choices = [];
    for (let i = 0; i < 4; i++) {
      choices.push(answer + i - index);
    }
    return shuffle(choices);
}
