import { randomizer, shuffle } from "./common";

export function create2DigitsSubList() {
    const list = [];

    for (let i = 1; i <= 100; i++) {
      for (let j = 0; j <= i/10; j++) {
        for (let k = 0; k <= i % 10; k++) {
          const num3 = i - j;

        list.push({
          num1: i,
          num2: j,
          num3,
          ops: "-",
          ...get2DigitsCommonProblem([i, j, num3]),
        });
        }

      }
    }

    return list;
}

export function create2DigitsAddList() {
    const list = [];

    for (let i = 1; i <= 100; i++) {
      for (let j = 1; j <= 100 - i; j++) {
        const num3 = i + j;

        list.push({
          ...get2DigitsCommonProblem([i, j, num3]),
          num1: i,
          num2: j,
          num3,
          ops: "+",

        });
      }
    }

    return list;
}

export function get2DigitsChoices(answer: number): number[] {
    const min = answer > 97 ? answer - 97 : 0;
    const max = answer > 3 ? 3 : answer;
    const index = randomizer(min, max);

    const choices = [];
    for (let i = 0; i < 4; i++) {
      choices.push(answer + i - index);
    }
    return shuffle(choices);
}

export function get2DigitsCommonProblem(arr: number[]) {
  const question = randomizer(0, arr.length - 1);
  const answer = arr[question];
  return {
      question,
      answer,
      choices: get2DigitsChoices(answer),
      equalRight: randomizer(0, 1) === 1,
  }
}
