import React, { useState } from 'react';
import classNames from 'classnames';

import { useAppSelector, useAppDispatch } from '../../app/hooks';

import styles from './Math1.module.css';
import { next, selectAnswer, selectCorrect, selectProblem, selectTotal } from './math1Slice';

export function Math1() {
  const problem = useAppSelector(selectProblem);
  const correct = useAppSelector(selectCorrect);
  const total = useAppSelector(selectTotal);

  const dispatch = useAppDispatch();
  const [selected, setSelected] = useState(-1);

  const { num1, num2, ops, choices, answer } = problem;

  const handleSelect = (sel: number) => {
    if (selected === -1) {
      setSelected(sel);
      dispatch(selectAnswer(sel));
    }
  }

  const handleNext = () => {
    setSelected(-1);
    dispatch(next());
  }

  return (
    <div>
      <div className={styles.row}>{correct}/{current}/{total}</div>
      <div className={styles.row}>{num1} {ops} {num2} = ?</div>
      <div className={styles.row}>
        {choices.map((c) => (
          <button
            className={classNames(styles.button, 
              selected !== -1 && c === answer && styles.correct,
              selected !== -1 && selected === c && c !== answer && styles.incorrect)}
            aria-label={c+""}
            onClick={() => handleSelect(c)}
          >
          {c}
        </button>
        ))}
      </div>
      <div className={styles.row}>
        <button
          className={styles.button}
          aria-label="Next"
          onClick={handleNext}
        >
          Next
        </button>
      </div>
    </div>
  );
}
