function randomizer(min: number, max: number) { // min and max included 
  return Math.floor(Math.random() * (max - min + 1) + min);
}

export function getProblem() {
  const ops = randomizer(0, 1);
  const num1 = randomizer(0, 10);
  const num2 = ops === 0 ? randomizer(0, 10-num1) : randomizer(0, num1);
  const answer = ops === 0 ? num1+num2 : num1-num2;
  let choices = [];
  while(true) {
    const index = randomizer(0, 3);
    switch (index) {
      case 0:
        choices = [answer, answer+1, answer+2, answer+3];
        break;
      case 1:
        choices = [answer-1, answer, answer+1, answer+2];
        break;
      case 2:
        choices = [answer-2, answer-1, answer, answer+1];
        break;
      default:
        choices = [answer-2, answer-1, answer, answer+1];
        break;
    }

    if (choices.every((c) => c >= 0 && c <= 10)) {
      break;
    }
  }

  return {
    num1,
    num2,
    ops: ["+","-"][ops],
    choices,
    answer,
  }
}
