import { randomizer, shuffle } from "./common";

export type CommonProblem = {
    answer: number;
    choices: number[];
    question: number;
    equalRight: boolean;
}

export function create1DigitSubList() {
    const list = [];

    for (let i = 1; i <= 10; i++) {
      for (let j = 1; j < i; j++) {
        const num3 = i - j;

        list.push({
          num1: i,
          num2: j,
          num3,
          ops: "-",
          ...getCommonProblem([i, j, num3]),
        });
      }
    }

    return list;
}

export function create1DigitAddList() {
    const list = [];

    for (let i = 1; i <= 10; i++) {
      for (let j = 1; j <= 10 - i; j++) {
        const num3 = i + j;

        list.push({
          ...getCommonProblem([i, j, num3]),
          num1: i,
          num2: j,
          num3,
          ops: "+",

        });
      }
    }

    return list;
}

export function get1DigitChoices(answer: number): number[] {
    const min = answer > 7 ? answer - 7 : 0;
    const max = answer > 3 ? 3 : answer;
    const index = randomizer(min, max);

    const choices = [];
    for (let i = 0; i < 4; i++) {
      choices.push(answer + i - index);
    }
    return shuffle(choices);
}

export function getCommonProblem(arr: number[]) {
    const question = randomizer(0, arr.length - 1);
    const answer = arr[question];
    return {
        question,
        answer,
        choices: get1DigitChoices(answer),
        equalRight: randomizer(0, 1) === 1,
    }
}
