import { createSlice, Draft, PayloadAction } from '@reduxjs/toolkit';
import { getProblemList } from './math1API';
import { RootState } from '../../app/store';

export interface Problem {
  num1: number;
  num2: number;
  ops: string;
  choices: number[];
  answer: number;
}

export interface Math1State {
  list: Problem[];
  current: number;
  correct: number;
}

const initialState: Math1State = {
  list: getProblemList(),
  correct: 0,
};

export const math1Slice = createSlice({
  name: 'math1',
  initialState,
  // The `reducers` field lets us define reducers and generate associated actions
  reducers: {
    next: (
      state: Draft<Math1State>,
    ) => {
      state.current = getProblem();
    },
    selectAnswer: (
      state: Draft<Math1State>,
      action: PayloadAction<number>,  
    ) => {
      action.payload === state.current.answer && state.correct++;
      state.total++;
    },
    reset: (state) => {
      state = {
        ...initialState,
      }
    },
  },
});

export const { next, selectAnswer, reset } = math1Slice.actions;

export const selectProblem = (state: RootState) => state.math1.current;
export const selectTotal = (state: RootState) => state.math1.total;
export const selectCorrect = (state: RootState) => state.math1.correct;

export default math1Slice.reducer;
