export function getChoices(answer: number) {
  let choices = [];
  let min = answer > 7 ? answer-7 : 0;
  let max = answer > 3 ? 3 : answer;

  const index = randomizer(min, max);
  switch (index) {
    case 0:
      choices = [answer, answer+1, answer+2, answer+3];
      break;
    case 1:
      choices = [answer-1, answer, answer+1, answer+2];
      break;
    case 2:
      choices = [answer-2, answer-1, answer, answer+1];
      break;
    default:
      choices = [answer-3, answer-2, answer-1, answer];
      break;
  }

  return {
    num1,
    num2,
    ops: ["+","-"][ops],
    choices,
    answer,
  }
}

function randomizer(min: number, max: number) { // min and max included 
  return Math.floor(Math.random() * (max - min + 1) + min);
}
