import { shuffle, createAddList, createSubList, getChoices, randomizer } from "../../app/common/math";
import { Problem1 } from "../math1/math1Slice";
import { Problem2 } from "./math2Slice";

export function createProblemList() {
  const addList = createAddList();
  const subList = createSubList();

  const list: Problem2[] = [];
  addList.forEach((p1) => {
    list.push(...addList.filter((p2) => p2.num1 === p1.num3 && p1.num3 + p2.num2 <= 10).map((p2) => combineProblems(p1, p2, p1.num3 + p2.num2)));
    list.push(...subList.filter((p2) => p2.num1 === p1.num3 && p1.num3 - p2.num2 >= 0).map((p2) => combineProblems(p1, p2, p1.num3 - p2.num2)));
  });
  subList.forEach((p1) => {
    list.push(...addList.filter((p2) => p2.num1 === p1.num3 && p1.num3 + p2.num2 <= 10).map((p2) => combineProblems(p1, p2, p1.num3 + p2.num2)));
    list.push(...subList.filter((p2) => p2.num1 === p1.num3 && p1.num3 - p2.num2 >= 0).map((p2) => combineProblems(p1, p2, p1.num3 - p2.num2)));
  });
  return shuffle(list);
}

function combineProblems(p1: Problem1, p2: Problem1, num4: number): Problem2 {
  const question = randomizer(1, 4);
  const answer = question === 1 ? p1.num1 : question === 2 ? p1.num2 : question === 3 ? p2.num2 : num4;
  return {
    num1: p1.num1,
    num2: p1.num2,
    num3: p2.num2,
    num4,
    ops1: p1.ops,
    ops2: p2.ops,
    choices: getChoices(answer),
    question,
    answer,
  };
}
