export type CommonProblem = {
    answer: number;
    choices: number[];
    question: number;
    equalRight: boolean;
}

export function createSubList() {
    const list = [];

    for (let i = 1; i <= 10; i++) {
      for (let j = 1; j < i; j++) {
        const num3 = i - j;


        list.push({
          num1: i,
          num2: j,
          num3,
          ops: "-",
          question,
          answer,
          choices: getChoices(answer),
        });
      }
    }

    return list;
}

export function createAddList() {
    const list = [];

    for (let i = 1; i <= 10; i++) {
      for (let j = 1; j <= 10 - i; j++) {
        const num3 = i + j;
        const question = randomizer(1, 3);
        const answer = question === 1 ? i : question === 2 ? j : num3;

        list.push({
          ...getCommonProblem([i, j, num3]),
          num1: i,
          num2: j,
          num3,
          ops: "+",
          answer,
          question,
          choices: getChoices(answer),
        });
      }
    }

    return list;
}

export function getChoices(answer: number): number[] {
    const min = answer > 7 ? answer - 7 : 0;
    const max = answer > 3 ? 3 : answer;
    const index = randomizer(min, max);

    const choices = [];
    for (let i = 0; i < 4; i++) {
      choices.push(answer + i - index);
    }
    return shuffle(choices);
}

export function randomizer(min: number, max: number) {
    // min and max included
    return Math.floor(Math.random() * (max - min + 1) + min);
}

export function shuffle(array: any[]) {
    for (let i = 0; i < array.length; i++) {
      const j = randomizer(0, array.length - 1);
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
}

export function getCommonProblem(arr: number[]) {
    const question = randomizer(0, arr.length - 1);
    const answer = arr[question];
    return {
        question,
        answer,
        chocies: getChoices(answer),
        equalRight: randomizer(0, 1) === 1,
    }
}
